    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //this is where all the code for the conversion will sit
        String CurrencyAmount = jTextField1.getText(); //getting the input from the user

        if (jTextField1.getText().equals("")) //if the text field is blank, prompt user to enter a number
        {
            jTextField1.requestFocus(); //so you dont have to relock the text field every time an error message pops up
            JOptionPane.showMessageDialog(this, "Please enter a number");
        }

        for (int i = 0; i < CurrencyAmount.length(); i++) //looping through all the digits in the string to make sure no letters or other items exist
        {
            if (!Character.isDigit(CurrencyAmount.charAt(i)) && !CurrencyAmount.contains(".")) //if character is not a digit and also is not a decimal            
            {
                JOptionPane.showMessageDialog(this, "Please enter a valid number");
                jTextField1.setText("");
                jTextField1.requestFocus(); //so you dont have to relock the text field every time an error message pops up
                return; //to prevent the program from spamming the error message for every item in the string
            }
        }
        jTextField2.setText(""); //resets the result text field for the next conversion
        String CurrencyFromText = jComboBox1.getSelectedItem().toString();
        String CurrencyToText = jComboBox2.getSelectedItem().toString();

        String[] temp = null; //creating empty array
        temp = CurrencyFromText.split(" - "); // splitting the name of the currency and the currency symbol
        String CurrencyFromTitle = temp[0];
        String CurrencyFromCode = temp[1];
        temp = CurrencyToText.split(" - "); // splitting the name of the currency and the currency symbol
        String CurrencyToTitle = temp[0];
        String CurrencyToCode = temp[1];

        String sourceAddress = "http://finance.yahoo.com/d/quotes.csv?f=l1&s=" + CurrencyFromCode + CurrencyToCode + "=X";
        String formattedSourceAddress = sourceAddress.replaceAll("\\s+", ""); //removing white spaces caused from how I entered the values into the ComboBoxes

        try {
            URL url = new URL(formattedSourceAddress);
            BufferedReader reader = new BufferedReader(new InputStreamReader(url.openStream()));
            String line = reader.readLine();
            double conversion = Double.parseDouble(CurrencyAmount) * Double.parseDouble(line); //multiplying the value times the conversion rate         
            jTextField2.setText(String.format("%,.2f", conversion)); //formatting text for the text box
            reader.close();

        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Unable to retrieve value from Yahoo Finance. Please try a different currency.");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

